@app.route('/api/random-dog', methods=['GET'])
def get_random_dog():
    try:
        # Choose a random dog image URL from the list
        random_image_url = random.choice(DOG_IMAGE_URLS)

        return jsonify({'status': 'success', 'image_url': random_image_url})

    except Exception as e:
        return jsonify({'status': 'error', 'message': f'Error: {str(e)}'}), 500

if __name__ == '__main__':
    app.run(debug=True)
